import { View } from '../components/Themed';
import React, {useEffect, useState} from "react";
import {
  ActionSheet,
  Button,
  Container,
  Content,
  Form,
  Icon,
  Input,
  Item,
  Label,
  Left,
  List,
  ListItem,
  Right,
  Subtitle,
  Text,
  Title
} from "native-base";
import {createStackNavigator} from "@react-navigation/stack";
import {Client} from "../entities/client";
import {Div} from "react-native-magnus";
import {connect} from "react-redux";
import {createClient, fetchClients, removeClient, updateClient} from "../services/clients-service";
import {fetchFactures} from "../services/factures-service";
import {ActivityIndicator} from "react-native";

export default function FacturesScreen() {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Factures view</Text>
    </View>
  );
}

const ClientsList = ({navigation, clients, fetchClients}: any) => {
  // const [clients, setClients] = useState([
  //     {
  //         id: "XXX_XX",
  //         name: "Oussama",
  //         email: "o@g.c"
  //     },
  //     {
  //         id: "ZZZZ",
  //         name: "Khalil",
  //         email: "kh@g.c"
  //     },
  // ] as Client[]);

  useEffect(
      () => {
        fetchClients();
        console.log("Fetched clients", clients);
      }, []);


  return <Container>
    <Content padder>
      <Button style={{width: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center'}} primary
              onPress={() => navigation.navigate('ClientForm',)}>
        <Text style={{textAlign: 'center'}}>
          Ajouter
        </Text>
      </Button>
      <Div style={{height: 20}}/>
      {/*<Text>{JSON.stringify(clients)}</Text>*/}
      {clients && clients.length === 0 && <Text style={{textAlign: 'center'}}>
        Aucun client ajout√©.
      </Text>}
      <List>
        {clients && clients.map((client: Client, key: number) => <ListItem button key={key}
                                                                           onPress={() => navigation.navigate('ClientDetails', {
                                                                             client,
                                                                             navigate: navigation.navigate
                                                                           })}>
              <Left>
                <Text>{client.account?.firstName} {client.account?.lastName}</Text>
              </Left>
              <Right>
                <Icon name="arrow-forward"/>
              </Right>
            </ListItem>
        )}
      </List>
    </Content>
  </Container>
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
});
