import React, {useEffect, useState} from "react";
import {FlatList, SafeAreaView, StyleSheet, View, Image} from "react-native";
import {createStackNavigator} from "@react-navigation/stack";
import {Container, Header, Content, Card, CardItem, Text, Icon, Right, Thumbnail, Body} from 'native-base';
import {
  ActionSheet,
  Button,
  Form,
  Input,
  Item,
  Label,
  Left,
  List,
  ListItem,
  Picker,
  Subtitle,
  Title
} from "native-base";
import {Compte} from "../entities/compte";
import {Div} from "react-native-magnus";
import {connect, Provider} from 'react-redux';
import {bindActionCreators} from "redux";
import {useKeycloak} from "expo-keycloak";
import {Client} from "../entities/client";
import {CreancierActions} from "../redux/reducers/creancier-reducer";
import {fetchCreanciers} from "../services/creanciers-service";
import {fetchAllCreanciers, fetchCreances, fetchCreancier} from "../services/factures-service";
import {Creancier} from "../entities/creancier";
import {applyMiddleware, createStore} from 'redux';
import rootReducer from "../redux/reducers";
import thunk from "redux-thunk";
import {getCreanciers} from "../redux/actions/creanciers-actions";
import {green50} from "react-native-paper/lib/typescript/styles/colors";
import {images} from '../images'


const CreanciersList = ({navigation, creanciers , fetchCreanciers }: any) => {

  useEffect(
      () => {
        fetchCreanciers().then(()=> console.log("Fetched Creanciers 1", creanciers))
            .catch((e:any)=> console.log("Error", e));
      }, []);
  return <Container>
    <Content padder>
      <Div style={{height: 20}}/>
      {creanciers && creanciers.length === 0 && <Text style={{textAlign: 'center'}}>
        Aucun creancier ajouté.
      </Text>}
      <Card>
      <CardItem>
        <Left>
          <Thumbnail style={styles.thumbnail} source={require('../assets/icons/services.png')} />
          <Body>
            <Text style={styles.title}>Les Creancier</Text>
            <Text note style={styles.dispo}>disponibles</Text>
          </Body>
        </Left>
      </CardItem>
        </Card>
        <List style={styles.list}>
        {/*{creanciers && creanciers.map((creancier: Creancier, key: number) =>*/}
        {/*    <ListItem button key={key}*/}
        {/*              onPress={() => navigation.navigate('ProfilStack', {*/}
        {/*               creancier,*/}
        {/*                navigate: navigation.navigate*/}
        {/*              })}>*/}

          <ListItem button onPress={() => navigation.navigate('ProfilStack', {creanciers, navigate: navigation.navigate})}>
            <Left>
              <Image style={styles.logo} source={require('../assets/icons/iam.png')} />
              <Body>
                <Text style={styles.title}>{creanciers[0]?.name}</Text>
                <Text note style={styles.sous}>Operateur</Text>
              </Body>
            </Left>
        </ListItem>

          <ListItem>
            <Left>
              <Image style={styles.logo} source={require('../assets/icons/rademaa.png')} />
              <Body>
                <Text style={styles.title}>{creanciers[1]?.name}</Text>
                <Text note style={styles.sous}>Eau && électricité</Text>
              </Body>
            </Left>
          </ListItem>

          <ListItem>
            <Left>
              <Image style={styles.logo} source={require('../assets/icons/orange.png')} />
              <Body>
                <Text style={styles.title}>{creanciers[2]?.name}</Text>
                <Text note style={styles.sous}>Operateur</Text>
              </Body>
            </Left>
          </ListItem>

      </List>
    </Content>
  </Container>
}

const mapStateToCreanciersListProps = (state: any) => ({
    creanciers: state.client.creanciers
});

const mapDispatchToCreanciersProps = (dispatch: any) => (
    bindActionCreators({
        getCreanciers,
    }, dispatch)
);

export const CreancierListState = connect(mapStateToCreanciersListProps, {fetchCreanciers})(CreanciersList);

const CreancesList = (props: any) => {
  const creancier = props.route.params.creancier
  console.log(props.route.params)

  return (<Container>
    <Content padder>
      {/*<Body >*/}
      <Subtitle style={{fontSize: 18, textAlign: "left"}}>Code de Creancier</Subtitle>
      <Title
          style={{fontSize: 36, textAlign: "left"}}>{creancier.codeCreancier} </Title>
      <Div style={{height: 20}}/>
      <Subtitle style={{fontSize: 18, textAlign: "left"}}>Name de Creancier</Subtitle>
      <Title style={{fontSize: 36, textAlign: "left"}}>{creancier.name}</Title>
      <Div style={{height: 20}}/>
      <Subtitle style={{fontSize: 18, textAlign: "left"}}>Category de Creancier</Subtitle>
      <Title style={{fontSize: 36, textAlign: "left"}}>{creancier.category}</Title>
      <Div style={{height: 20}}/>

    </Content></Container>)
      ;
}

export const CreancesListState = connect(null, { fetchCreances })(CreancesList);

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  logo: {
    width: 120,
    height: 100,
    marginRight : 20,
    resizeMode : "contain"
    // objectFit : "cover"
  }, list: {
    marginTop:10
  },thumbnail: {
    height:80,
    width:80
  },
  dispo: {
    fontFamily: "Cochin",
    color: 'green',
  },
  sous: {
    fontFamily: "Cochin",
    color: 'gray',
  },

});
