import * as React from 'react';
import {Image, StyleSheet} from 'react-native';

import EditScreenInfo from '../components/EditScreenInfo';
import { View } from '../components/Themed';
import {
  Container,
  Header,
  Content,
  Card,
  CardItem,
  Text,
  Icon,
  Right,
  Thumbnail,
  Body,
  Left,
  List,
  ListItem
} from 'native-base';
import {useEffect, useState} from "react";
import {Div} from "react-native-magnus";
import {Creancier} from "../entities/creancier";
import images from "../images";
import {bindActionCreators} from "redux";
import {getCreanciers} from "../redux/actions/creanciers-actions";
import {connect} from "react-redux";
import {fetchAllCreanciers} from "../services/factures-service";
import {fetchComptes} from "../services/form-service";
import {getComptes} from "../redux/actions/compte-actions";
import {Client} from "../entities/client";
import {Compte} from "../entities/compte";

const ComptesList = ({navigation, creanciers , fetchComptes }: any) => {

  // useEffect(
  //     () => {
  //       fetchAllCreanciers().then(()=> console.log("Fetched Creanciers 1", creanciers))
  //           .catch((e:any)=> console.log("Error", e));
  //     }, []);

  const [comptes, setComptes] = useState([
      {
        numeroCompte: "568-515166141-788",
        intitule: "Khalil Daoulat",
        solde:1520
      },
    {
      numeroCompte: "568-515166141-788",
      intitule: "Khalil Daoulat",
      solde:3500

    },
    {
      numeroCompte: "568-54416841651-788",
      intitule: "Khalil Daoulat",
      solde:1000
    },
    {
      numeroCompte: "568-515166141-788",
      intitule: "Khalil Daoulat",
      solde:41649
    },
  ] as Compte[]);

  return <Container>
    <Content padder>

      <Card>
        <CardItem>
          <Left>
            <Image style={styles.thumbnail} source={require('../assets/icons/account.png')} />
            <Body>
              <Text style={styles.title}>Les Comptes</Text>
              <Text note style={styles.dispo}>Votre comptes disponble. </Text>
            </Body>
          </Left>
        </CardItem>
      </Card>
      <Div style={{height: 20}}/>
      {comptes && comptes.length === 0 && <Text style={{textAlign: 'center'}}>
        Aucun comptes ajout√©.
      </Text>}
      <List style={styles.list}>
        {comptes && comptes.map((compte: Compte, key: any) =>
            <ListItem button key={key}
                      onPress={() => navigation.push('CreancesScreen', {compte, navigate: navigation.navigate})}>
              <Left>
                <Image style={styles.logo} source={require('../assets/icons/wallet.png')} />
                <Body>
                  <Text style={styles.titleCreancier}>{compte.numeroCompte}</Text>
                  <Text note style={styles.sous}>{compte.intitule}  {compte.solde}.00 DH</Text>
                </Body>
              </Left>
            </ListItem>
        )}
      </List>
    </Content>
  </Container>
}

const mapStateToComptesListProps = (state: any) => ({
  creanciers: state.client.creanciers
});

const mapDispatchToComptesProps = (dispatch: any) => (
    bindActionCreators({
      getComptes,
    }, dispatch)
);

export const ComptesListState = connect(mapStateToComptesListProps, {fetchComptes})(ComptesList);


const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  logo: {
    width: 40,
    height: 40,
    marginRight : 20,
    resizeMode : "contain"
    // objectFit : "cover"
  }, list: {
    marginTop:10
  },thumbnail: {
    height:80,
    width:80,
    resizeMode : "contain"

  },
  dispo: {
    fontFamily: "Cochin",
    color: 'gray',
    fontSize:15,
    marginTop:10,
    fontWeight: 'bold'
  },
  sous: {
    fontFamily: "Cochin",
    color: 'gray',
  },
  titleCreancier: {
    fontSize: 17,
    fontWeight: 'bold',
  },
});
