import * as React from 'react';
import {Image, StyleSheet} from 'react-native';
import { Container, Header, Content, Card, CardItem, Text, Icon, Right, Body } from 'native-base';

import EditScreenInfo from '../components/EditScreenInfo';
import {  View } from '../components/Themed';
import {useEffect, useState} from "react";
import { List, ListItem} from "native-base";
import {Div} from "react-native-magnus";
import {Creancier} from "../entities/creancier";
import {bindActionCreators} from "redux";
import {getCreanciers} from "../redux/actions/creanciers-actions";
import {connect} from "react-redux";
import {fetchCreanciers} from "../services/creanciers-service";
import {getComptes} from "../redux/actions/compte-actions";
import {fetchComptes} from "../services/comptes-service";
import {Compte} from "../entities/compte";

export default function ComptesScreen() {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Comptes view</Text>
    </View>
  );
}

const ComptesList = ({navigation, compte , fetchComptes }: any) => {


  // useEffect(
  //     () => {
  //       fetchComptes().then(()=> console.log("Fetched Creanciers 1", comptes))
  //           .catch((e:any)=> console.log("Error", e));
  //     }, []);

  const [comptes, setComptes] = useState([
      {
          numeroCompte: '859-518461316-89',
          intitule: "Oussama Lahmidi",
          solde: 700
      },
      {
          numeroCompte: '223-54946144969-52',
          intitule: "Oussama Lahmidi",
          solde: 1000
      },
  ] as Compte[]);


  return <Container>
    <Content padder>
      <Div style={{height: 20}}/>
      {comptes && comptes.length === 0 && <Text style={{textAlign: 'center'}}>
        Aucun creanciers ajouté.
      </Text>}
      <List style={styles.list}>
        {comptes && comptes.map((compte: Compte, key: number) => <ListItem button key={key}
                                                                                       onPress={() => navigation.navigate('CreancierDetails', {
                                                                                         compte,
                                                                                         navigate: navigation.navigate
                                                                                       })}>
              {/*<Image style={styles.logo} source={require('../assets/icons/iam.png')}/>*/}
            {/*<Card>*/}
            {/*    <CardItem>*/}
                    {/*<Icon active name="logo-googleplus" />*/}

                    <Body>
                        <Text>{compte.intitule}</Text>
                        <Text note>{compte.numeroCompte}</Text>
                    </Body>
                    <Right>
                        <Icon name="arrow-forward" />
                    </Right>
                {/*</CardItem>*/}
            {/*</Card>*/}
            </ListItem>
        )}
      </List>
    </Content>
  </Container>
}

const mapStateToComptesListProps = (state: any) => ({
  creanciers: state.client.creanciers
});

const mapDispatchToComptesProps = (dispatch: any) => (
    bindActionCreators({
      getComptes,
    }, dispatch)
);

export const ComptesListState = connect(mapStateToComptesListProps, {fetchComptes})(ComptesList);



const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },logo: {
    width: 120,
    height: 100,
    marginRight : 20,
    resizeMode : "contain"
    // objectFit : "cover"
  }, list: {
    marginTop:70
  }
});
