import * as React from 'react';
import {Image, StyleSheet} from 'react-native';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';
import {useEffect} from "react";
import {Body, Card, CardItem, Container, Content, Left, List, ListItem, Thumbnail} from "native-base";
import {Div} from "react-native-magnus";
import {Creancier} from "../entities/creancier";
import images from "../images";
import {bindActionCreators} from "redux";
import {getCreanciers} from "../redux/actions/creanciers-actions";
import {connect} from "react-redux";
import {fetchAllCreanciers} from "../services/factures-service";
import {fetchComptes} from "../services/form-service";
import {getComptes} from "../redux/actions/compte-actions";

const ComptesList = ({navigation, creanciers , fetchAllCreanciers }: any) => {

  useEffect(
      () => {
        fetchAllCreanciers().then(()=> console.log("Fetched Creanciers 1", creanciers))
            .catch((e:any)=> console.log("Error", e));
      }, []);
  return <Container>
    <Content padder>

      <Card>
        <CardItem>
          <Left>
            <Thumbnail style={styles.thumbnail} source={require('../assets/icons/services.png')} />
            <Body>
              <Text style={styles.title}>Les Creancier</Text>
              <Text note style={styles.dispo}>Notre service externe desponible que vous pouvez obtenir votre facture et le payer. </Text>
            </Body>
          </Left>
        </CardItem>
      </Card>
      <Div style={{height: 20}}/>
      {creanciers && creanciers.length === 0 && <Text style={{textAlign: 'center'}}>
        Aucun creancier ajout√©.
      </Text>}
      <List style={styles.list}>
        {creanciers && creanciers.map((creancier: Creancier, key: any) =>
            <ListItem button key={key}
                      onPress={() => navigation.push('CreancesScreen', {creancier, navigate: navigation.navigate})}>
              <Left>
                <Image style={styles.logo} source={images[creancier.codeCreancier].uri} />
                <Body>
                  <Text style={styles.titleCreancier}>{creancier.name}</Text>
                  <Text note style={styles.sous}>{creancier.codeCreancier}</Text>
                </Body>
              </Left>
            </ListItem>
        )}
      </List>
    </Content>
  </Container>
}

const mapStateToCreanciListProps = (state: any) => ({
  creanciers: state.client.creanciers
});

const mapDispatchToComptesProps = (dispatch: any) => (
    bindActionCreators({
      getComptes,
    }, dispatch)
);

export const CreancierListState = connect(mapDispatchToComptesProps, {fetchComptes})(ComptesList);


const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
});
