import React, {useEffect, useState} from "react";
import {FlatList, SafeAreaView, StyleSheet, View, Image} from "react-native";
import {createStackNavigator} from "@react-navigation/stack";
import { Container, Header, Content, Card, CardItem, Text, Icon, Right } from 'native-base';
import {
  ActionSheet,
  Button,
  Form,
  Input,
  Item,
  Label,
  Left,
  List,
  ListItem,
  Picker,
  Subtitle,
  Title
} from "native-base";
import {Compte} from "../entities/compte";
import {Div} from "react-native-magnus";
import {connect, Provider} from 'react-redux';
import {bindActionCreators} from "redux";
import {useKeycloak} from "expo-keycloak";
import {Client} from "../entities/client";
import {CreancierActions} from "../redux/reducers/creancier-reducer";
import {fetchCreanciers} from "../services/creanciers-service";
import {fetchAllCreanciers, fetchCreancier} from "../services/factures-service";
import {Creancier} from "../entities/creancier";
import {applyMiddleware, createStore} from 'redux';
import rootReducer from "../redux/reducers";
import thunk from "redux-thunk";
import {getCreanciers} from "../redux/actions/creanciers-actions";


const CreanciersList = ({navigation, creanciers , fetchCreanciers }: any) => {


  useEffect(
      () => {
        fetchCreanciers().then(()=> console.log("Fetched Creanciers 1", creanciers))
            .catch((e:any)=> console.log("Error", e));
      }, []);


  return <Container>
    <Content padder>
      <Div style={{height: 20}}/>
      {creanciers && creanciers.length === 0 && <Text style={{textAlign: 'center'}}>
        Aucun creanciers ajout√©.
      </Text>}
      <List style={styles.list}>
        {creanciers && creanciers.map((creancier: Creancier, key: number) =>
            <ListItem button key={key}
                      onPress={() => navigation.navigate('FacturesScreen', {
                        creancier,
                        navigate: navigation.navigate
                      })}>
                    <Image style={styles.logo} source={require('../assets/icons/'+creancier.codeCreancier+'.png')}/>
                  <Text>{creancier.name}</Text>

            </ListItem>
        )}
      </List>
    </Content>
  </Container>
}

const mapStateToCreanciersListProps = (state: any) => ({
    creanciers: state.client.creanciers
});

const mapDispatchToCreanciersProps = (dispatch: any) => (
    bindActionCreators({
        getCreanciers,
    }, dispatch)
);

export const CreancierListState = connect(mapStateToCreanciersListProps, {fetchCreanciers})(CreanciersList);


const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  logo: {
    width: 120,
    height: 100,
    marginRight : 20,
    resizeMode : "contain"
    // objectFit : "cover"
  }, list: {
    marginTop:40
  }

});
