import { useKeycloak } from "expo-keycloak";
import React, { useState } from "react";
import {Text, View, StyleSheet, TextInput, Button, ActivityIndicator, Image, ImageBackground} from "react-native";
import { Div } from "react-native-magnus";
import {keycloak, login} from "../services/auth.service";
import {AuthSession} from "expo/build/removed.web";
import {Left} from "native-base";

const initialState = {
  username: "",
  password: "",
};

export const LoginView = (props: any) => {
  const [credentials, setCredentials] = useState(initialState);
  const {ready, login, isLoggedIn, token, logout,} = useKeycloak();
  if (!ready) return <ActivityIndicator />;
  const handleLogin = async () => {
    props.login();
  };

  return (
      <View style={styles.container}>
        <ImageBackground style={ styles.imgBackground }
               resizeMode='cover'  source={require('../assets/images/background.jpeg')}/>
        <Text style={styles.title}>Welcome to ENSAPAY</Text>
        <Text style={styles.title}>Client Side</Text>
        <Button title="Se connecter" onPress={handleLogin}>
          Hello world
        </Button>
      </View>
  );
};

const styles = StyleSheet.create({
  title: {
    fontSize: 24,
    fontWeight: "bold",
  },
  subtitle: {
    fontSize: 16,
    opacity: 0.7,
  },
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  inputField: {
    width: "80%",
    backgroundColor: "#eee",
    borderRadius: 5,
    height: 50,
    marginBottom: 10,
    justifyContent: "center",
    padding: 20,
    fontSize: 18,
  },
  mainButton: {
    width: "80%",
    backgroundColor: "blue",
    borderRadius: 5,
    height: 50,
    marginBottom: 10,
    justifyContent: "center",
    padding: 20,
  },imgBackground: {
    width: '100%',
    height: '100%',
    flex: 1
  },
});
