import {View } from '../components/Themed';
import React, {useEffect, useState} from "react";
import {loadUserInfo} from "../services/auth.service";
import {ActionSheet, Button, Container, Content, ListItem, Subtitle, Text, Title} from 'native-base';
import {useKeycloak} from "expo-keycloak";
import {Div} from "react-native-magnus";
import {StyleSheet} from "react-native";
import {config} from "../constants/api";
import * as Linking from 'expo-linking';
import * as WebBrowser from 'expo-web-browser';
import FormScreen from "../views/FormScreen";
import {Client} from "../entities/client";

export default function ProfilScreen(props: any) {
  const [currentUser, setCurrentUser] = useState({} as any);
  const {
      ready,
      login,
      isLoggedIn,
      token,
      logout,
  } = useKeycloak();
  useEffect(() => {
          if (ready)
              loadUserInfo(token)
                  .then(response => setCurrentUser(response.data))
      logout()
      }
      , [ready]);
  return (
    <View style={styles.container}>
      <Container>
        <Content padder>
            {/*<Body >*/}
            <Subtitle style={{ fontSize: 22, textAlign: "left",fontWeight:"bold",marginTop:"10%", color:"black" }}>Informations Personnelles:</Subtitle>

            <Title style={{ fontSize: 18, textAlign: "left",marginTop:"10%",color:"black" }}>Non et prenom :{currentUser?.name}</Title>

            <Title style={{ fontSize: 18, textAlign: "left",marginTop:"10%",color:"black" }}>Email : {currentUser?.email}</Title>

            <Title style={{fontSize: 18, textAlign: "left",marginTop:"10%",color:"black"}}>Numero de tele : {currentUser?.numeroTel}</Title>

            <Button style={{ width: '100%', display: 'flex',marginTop:"30%", alignItems: 'center', justifyContent: 'center' }} warning
            onPress={() =>
              props.route.params.navigate('FormScreen',)
            }

            >
                <Text style={{ textAlign: 'center' }}>
                    Voir Tous Les Factures
                </Text>
            </Button>

            <Button style={{ width: '100%', display: 'flex',marginTop:"5%", alignItems: 'center', justifyContent: 'center' }} primary 
             onPress={() => WebBrowser.openBrowserAsync(`${config.AUTH_SERVER}/realms/ensapay/account`)} >
                <Text style={{ textAlign: 'center' }}>
                    Modifier vos informations
                </Text>
            </Button>



            <Button style={{ marginTop: "auto",width: '100%', display: 'flex' ,alignItems: 'center', justifyContent: 'center' }} danger  onPress={() => {logout()}
            }>
                <Text style={{ textAlign: 'center' }}>
                    Deconnexion

                </Text>
            </Button>

        </Content></Container>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
});
